"""
Given a string s and a dictionary of words dict, determine if s can be segmented into a space-separated sequence of one or more dictionary words.

For example, given
s = "leetcode",
dict = ["leet", "code"].

Return true because "leetcode" can be segmented as "leet code".
"""

#=> recursive solution O(2**N)

def wordBreak1(s, wordDict):
    """
    :type s: str
    :type wordDict: Set[str]
    :rtype: bool
    """
    if len(s)==0:
        return True
    if len(wordDict)==0:
        return False
    match = [i for i in wordDict if i in s]
    #print s,match
    if len(match)==0:
        return False
    for i in match:
        ind = s.index(i)
        length = len(i)
        temp = list(s)
        temp = temp[:ind]+temp[ind+length:]
        p = "".join(temp)
        if wordBreak(p,wordDict):
            return True
    return False

#=> recursive solution using DP -> O(N**2)

def wordBreak2(self, s, wordDict):
    """
    :type s: str
    :type wordDict: Set[str]
    :rtype: bool
    """
    if len(s)==0:
        return True
    if len(wordDict)==0:
        return False
    #dp[i] is 1 if s[i:] can be broken into words using wordDict
    dp = [0 for i in range(len(s)+1)]
    # (0)->unchecked, (1)->true, (-1)->false
    self.wordBreakAux(s,0,dp,wordDict)
    return dp[0]==1

def wordBreakAux(self,s,start,dp,wordDict):
    if start==len(s):
        dp[start]=1
        return 1
    if dp[start]!=0:
        return dp[start]
    result = -1
    for i in range(start+1,len(s)+1):
        x = s[start:i]
        if x in wordDict:
            result = self.wordBreakAux(s,i,dp,wordDict)
            if result==1:
                dp[start] = result
                return result
    dp[start] = result
    return result

